import Vue from 'vue'
import Router from 'vue-router'
import Home from './views/Home.vue'
import About from './views/About.vue'
// import的名字是自己定义的
import UserCenter from './views/user-center.vue'

Vue.use(Router)

// import的时候不用加{}
export default new Router({
  routes: [
    {
      path: '/',
      name: 'home',
      component: Home
    },
    {
      path: '/about',
      name: 'about',
      component: About
    },
    {
      path: '/center',
      // 用来在方法中进行路由跳转的时候使用
      name: 'center',
      component: UserCenter
    }
  ]
})
